function simple_slide(e){function t(t){if(t){var r="-="+e.w}else{var r="+="+e.w}e.ul.stop(0,1).animate({"margin-left":r},500,e.efecto_animacion,function(){n()})}function n(){var t=parseInt(e.ul.css("margin-left"));if(0<=t){e.ul.stop(0,1).animate({"margin-left":0},100);e.prev.hide()}else{e.prev.show()}if(t+e.wContent<=e.w){e.ul.stop(0,1).animate({"margin-left":e.w-e.wContent},100);e.next.hide()}else{e.next.show()}}e.lis=$("li",e.ul);e.w=$(e.lis[0]).width();e.wContent=e.lis.length*e.w;e.ul.css({width:e.wContent});e.next.click(function(){t(1);return false});e.prev.click(function(){t(0);return false});n()}